import{_ as t,c as r,o as a,ah as l}from"./chunks/framework.BaaE5B5v.js";const f=JSON.parse('{"title":"CORS 预检请求（preflight request）","description":"","frontmatter":{"title":"CORS 预检请求（preflight request）","date":"2025-03-15T23:08:14.000Z","lastmod":"2025-03-15T23:08:41.000Z","tags":["Web 前端","JavaScript","HTTP"]},"headers":[],"relativePath":"content/description-cors-preflight-request.md","filePath":"content/description-cors-preflight-request.md"}'),i={name:"content/description-cors-preflight-request.md"};function o(s,e,n,p,c,d){return a(),r("div",null,[...e[0]||(e[0]=[l('<h1 id="cors-预检请求-preflight-request" tabindex="-1">CORS 预检请求（preflight request） <a class="header-anchor" href="#cors-预检请求-preflight-request" aria-label="Permalink to “CORS 预检请求（preflight request）”">​</a></h1><h2 id="内容来源" tabindex="-1">内容来源 <a class="header-anchor" href="#内容来源" aria-label="Permalink to “内容来源”">​</a></h2><ul><li><a href="https://developer.mozilla.org/zh-CN/docs/Glossary/Preflight_request" target="_blank" rel="noreferrer">Preflight request - 术语表 | MDN</a></li><li><a href="https://developer.mozilla.org/zh-CN/docs/Web/HTTP/CORS" target="_blank" rel="noreferrer">跨源资源共享（CORS） - HTTP | MDN</a></li></ul><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to “简介”">​</a></h2><p>CORS 预检请求主要用于检测服务器是否支持 CORS（跨域资源共享）。</p><p>当有必要的时候，浏览器会自动发出一个预检请求；因此在正常情况下，前端开发者不需要自己去发这样的请求。</p><h2 id="触发条件" tabindex="-1">触发条件 <a class="header-anchor" href="#触发条件" aria-label="Permalink to “触发条件”">​</a></h2><p>某些请求（以下称其为 “简单请求”）不会触发 CORS 预检请求。<br> 若请求满足所有下述条件，则该请求可视为 “简单请求”：</p><ul><li><p>使用下列请求方式之一</p><ul><li>GET</li><li>HEAD</li><li>POST</li></ul></li><li><p>除了被用户代理自动设置的 Header（例如 Connection、User-Agent）和其他不在 Fetch 规范中定义的 <a href="https://fetch.spec.whatwg.org/#forbidden-header-name" target="_blank" rel="noreferrer">禁用的 Header 名称</a> ，允许人为设置下列 <a href="https://fetch.spec.whatwg.org/#cors-safelisted-request-header" target="_blank" rel="noreferrer">对 CORS 安全的 Header 字段</a></p><ul><li>Accept</li><li>Accept-Language</li><li>Content-Language</li><li>Content-Type（需要注意额外的限制）</li></ul></li><li><p>在 Header 中，<code>Content-Type</code> 的值为下列之一</p><ul><li>text/plain</li><li>multipart/form-data</li><li>application/x-www-form-urlencoded</li></ul></li><li><p>请求中的任意 <code>XMLHttpRequest</code> 对象均没有注册任何事件监听器；可以通过 <code>XMLHttpRequest.upload</code> 属性访问 <code>XMLHttpRequest</code> 对象。</p></li><li><p>请求中没有使用 <code>ReadableStream</code> 对象。</p></li></ul><p>‍</p>',10)])])}const u=t(i,[["render",o]]);export{f as __pageData,u as default};
