import{_ as e,c as r,o as i,ah as l}from"./chunks/framework.ByubJJ5I.js";const f=JSON.parse('{"title":"「Minecraft」OP 权限等级","description":"","frontmatter":{"title":"「Minecraft」OP 权限等级","date":"2025-03-19T12:32:49.000Z","lastmod":"2025-03-19T12:35:47.000Z","tags":["Minecraft","科普"]},"headers":[],"relativePath":"content/minecraft-op-permission-level.md","filePath":"content/minecraft-op-permission-level.md"}'),t={name:"content/minecraft-op-permission-level.md"};function n(o,a,c,s,h,d){return i(),r("div",null,[...a[0]||(a[0]=[l('<h1 id="「minecraft」op-权限等级" tabindex="-1">「Minecraft」OP 权限等级 <a class="header-anchor" href="#「minecraft」op-权限等级" aria-label="Permalink to “「Minecraft」OP 权限等级”">​</a></h1><p>权限等级用于描述玩家或命令执行者的权限。</p><hr><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to “参考”">​</a></h2><ul><li><a href="https://zh.minecraft.wiki/w/%E6%9D%83%E9%99%90%E7%AD%89%E7%BA%A7" target="_blank" rel="noreferrer">权限等级 - Minecraft Wiki</a></li></ul><h2 id="效果" tabindex="-1">效果 <a class="header-anchor" href="#效果" aria-label="Permalink to “效果”">​</a></h2><h3 id="java-版" tabindex="-1">Java 版 <a class="header-anchor" href="#java-版" aria-label="Permalink to “Java 版”">​</a></h3><ul><li>1级：玩家可无视重生点保护。</li><li>2级：玩家或执行者可以使用更多命令（详见<a href="https://minecraft.fandom.com/wiki/Commands" target="_blank" rel="noreferrer">命令</a>页面），玩家可以使用命令方块。（作弊命令）</li><li>3级：玩家或执行者可以使用更多命令。（多人管理）</li><li>4级：玩家或执行者可以使用所有命令。（服务器管理）</li></ul><h3 id="基岩版" tabindex="-1">基岩版 <a class="header-anchor" href="#基岩版" aria-label="Permalink to “基岩版”">​</a></h3><p>玩家可以使用对应其等级的命令，拥有更高权限等级的玩家可以使用需要较低权限等级的命令。</p><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to “定义”">​</a></h2><h3 id="java-版-1" tabindex="-1">Java 版 <a class="header-anchor" href="#java-版-1" aria-label="Permalink to “Java 版”">​</a></h3><p>在 Java 版中，可用的权限等级为 0、1、2、3 以及 4。</p><ul><li><p>命令方块或命令方块矿车拥有权限等级 2。</p></li><li><p>服务器控制台拥有权限等级 4。</p></li><li><p>函数（Function）拥有权限等级 2（可以在 <code>server.properties</code> 中修改）。</p></li><li><p>对于玩家：</p><ul><li>若玩家在服务器中拥有 OP 权限，其权限等级可在服务端的 <code>ops.json</code>​ 文件中指定，默认权限等级可在 <code>server.properties</code> 中设置。</li><li>若玩家处于单人世界或为局域网世界的所有者（房主），且启用了作弊，则其权限等级为 4。</li></ul></li></ul><h3 id="基岩版-1" tabindex="-1">基岩版 <a class="header-anchor" href="#基岩版-1" aria-label="Permalink to “基岩版”">​</a></h3><p>在基岩版中，可用的权限等级为 0、1、2、3 以及 4。</p>',16)])])}const m=e(t,[["render",n]]);export{f as __pageData,m as default};
