import{_ as a,c as i,o as s,ah as d}from"./chunks/framework.ByubJJ5I.js";const c=JSON.parse('{"title":"Windows mklink 命令","description":"","frontmatter":{"title":"Windows mklink 命令","date":"2025-03-15T22:29:09.000Z","lastmod":"2025-03-15T22:30:51.000Z","tags":["Windows","命令","命令提示符"]},"headers":[],"relativePath":"content/windows-command-mklink.md","filePath":"content/windows-command-mklink.md"}'),n={name:"content/windows-command-mklink.md"};function e(l,t,r,h,k,p){return s(),i("div",null,[...t[0]||(t[0]=[d(`<h1 id="windows-mklink-命令" tabindex="-1">Windows mklink 命令 <a class="header-anchor" href="#windows-mklink-命令" aria-label="Permalink to “Windows mklink 命令”">​</a></h1><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to “参考”">​</a></h2><ul><li><a href="https://docs.microsoft.com/zh-cn/windows-server/administration/windows-commands/mklink" target="_blank" rel="noreferrer">mklink | Microsoft Docs</a></li><li><a href="https://liam.page/2018/12/10/mklink-in-Windows/" target="_blank" rel="noreferrer">Windows 中的 mklink 命令 | 始终</a></li></ul><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to “简介”">​</a></h2><p>该命令用于在 Windows 系统上创建对于文件或目录的符号链接，类似 Linux 上的 <code>ln</code> 命令，但两者的功能不完全相同。</p><p>该命令创建的链接与 “快捷方式” 功能创建的链接并不相同。</p><p>使用该命令创建链接时需要拥有管理员权限，否则无法创建，并提示 “你没有足够的权限执行此操作”。</p><h2 id="命令语法" tabindex="-1">命令语法 <a class="header-anchor" href="#命令语法" aria-label="Permalink to “命令语法”">​</a></h2><div class="language-plaintext"><button title="Copy Code" class="copy"></button><span class="lang">plaintext</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>mklink [[/d] | [/h] | [/j]] link target</span></span>
<span class="line"><span></span></span>
<span class="line"><span>    /d      创建目录符号链接。</span></span>
<span class="line"><span>    /h      创建硬链接而非符号链接。</span></span>
<span class="line"><span>    /j      创建目录联接。</span></span>
<span class="line"><span>    link    指定要创建的符号链接的名称。</span></span>
<span class="line"><span>    target  指定新链接引用的路径（相对或绝对）。</span></span></code></pre></div><p>默认情况下，该命令创建的是文件符号链接。</p><h2 id="参数效果对比" tabindex="-1">参数效果对比 <a class="header-anchor" href="#参数效果对比" aria-label="Permalink to “参数效果对比”">​</a></h2><table tabindex="0"><thead><tr><th>描述</th><th>不带参数</th><th>/D 参数</th><th>/H 参数</th><th>/J 参数</th></tr></thead><tbody><tr><td>中文名称</td><td>符号链接</td><td>符号链接</td><td>硬链接</td><td>联接</td></tr><tr><td>英文名称</td><td>Symbolic Link</td><td>Symbolic Link</td><td>Hard Link</td><td>Junction</td></tr><tr><td>作用对象</td><td>文件</td><td>目录</td><td>文件</td><td>目录</td></tr><tr><td>是否一定指向路径</td><td>否</td><td>否</td><td>否</td><td>是</td></tr><tr><td>dir 类型</td><td>SYMLINK</td><td>SYMLINK</td><td>无特殊显示</td><td>JUNCTION</td></tr><tr><td>资源管理器类型</td><td>.symlink</td><td>文件夹</td><td>无特殊显示</td><td>文件夹</td></tr><tr><td>资源管理器图标</td><td>快捷方式</td><td>文件夹快捷方式</td><td>无特殊显示</td><td>文件夹快捷方式</td></tr><tr><td>修改同步</td><td>是</td><td>是</td><td>是</td><td>是</td></tr><tr><td>删除同步</td><td>否</td><td>否</td><td>否</td><td>否</td></tr><tr><td>彻底删除源</td><td>删除源路径</td><td>删除源路径</td><td>删除所有硬链接</td><td>删除源路径</td></tr><tr><td>引用错误报错</td><td>无</td><td>引用了不可用的位置</td><td>-</td><td>引用了不可用的位置</td></tr></tbody></table><h2 id="命令示例" tabindex="-1">命令示例 <a class="header-anchor" href="#命令示例" aria-label="Permalink to “命令示例”">​</a></h2><h3 id="将部分软件在-c-盘的数据转移到其他分区" tabindex="-1">将部分软件在 C 盘的数据转移到其他分区 <a class="header-anchor" href="#将部分软件在-c-盘的数据转移到其他分区" aria-label="Permalink to “将部分软件在 C 盘的数据转移到其他分区”">​</a></h3><p>例如</p><div class="language-bat"><button title="Copy Code" class="copy"></button><span class="lang">bat</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">@</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">REM</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> 1. 将文件夹移动到新的路径（例如 D:\\Minecraft\\Main\\.minecraft\\）</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">@</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">REM</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> 2. 执行 \`cd\` 命令，切换工作目录</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">cd</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> /d C:\\Users\\用户名\\AppData\\Roaming</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">@</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">REM</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> 3. 执行 \`mklink /j\` 命令，创建目录联接</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">mklink</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> /j .minecraft D:\\Minecraft\\Main\\.minecraft</span></span></code></pre></div><p>‍</p>`,17)])])}const m=a(n,[["render",e]]);export{c as __pageData,m as default};
